use image::GenericImageView;
use js_sys::Uint8Array;
use std::f64;
use wasm_bindgen::{prelude::*, Clamped, JsCast};
use wasm_bindgen_futures::JsFuture;
use web_sys::ImageData;

#[wasm_bindgen]
pub async fn fetch_url_binary(url: &str) -> Result<Uint8Array, JsValue> {
    let window = web_sys::window().unwrap(); // Browser window
    let promise = JsFuture::from(window.fetch_with_str(url)); // File fetch promise
    let result = promise.await?; // Await fulfillment of fetch
    let response: web_sys::Response = result.dyn_into().unwrap(); // Type casting
    let image_data = JsFuture::from(response.array_buffer()?).await?; // Get text
    Ok(Uint8Array::new(&image_data))
}

#[wasm_bindgen]
pub async fn draw_image(makeBlue: bool) -> Result<(), JsValue> {
    let window = web_sys::window().unwrap();
    let document = window.document().expect("Could not get document");
    let canvas = document
        .get_element_by_id("canvas")
        .unwrap()
        .dyn_into::<web_sys::HtmlCanvasElement>()?;
    let context = canvas
        .get_context("2d")?
        .unwrap()
        .dyn_into::<web_sys::CanvasRenderingContext2d>()?;
    // let img: &'static str  = "data:image/png;base64,";
    let url: &'static str = "https://picsum.photos/200/200";

    let binary = fetch_url_binary(url).await?;
    let altbuf = binary.to_vec();
    let image = image::load_from_memory_with_format(&altbuf, image::ImageFormat::Jpeg).unwrap();
    let mut rgba_image = image.to_rgba8();

    if makeBlue == true {
        for (_, _, pixel) in rgba_image.enumerate_pixels_mut() {
            if pixel[0] > 0 {
                *pixel = image::Rgba([0, pixel[1], pixel[2], pixel[3]]);
            }
        }
    }

    let clamped_buf: Clamped<&[u8]> = Clamped(rgba_image.as_raw());
    let image_data_temp =
        ImageData::new_with_u8_clamped_array_and_sh(clamped_buf, image.width(), image.height())?;
    context.put_image_data(&image_data_temp, 0.0, 0.0)?;
    Ok(())
    // context.draw_image_with_html_image_element(image, 0, 0);
}

#[wasm_bindgen(start)]
fn start() {
    let document = web_sys::window().unwrap().document().unwrap();
    let canvas = document.get_element_by_id("canvas").unwrap();
    let canvas: web_sys::HtmlCanvasElement = canvas
        .dyn_into::<web_sys::HtmlCanvasElement>()
        .map_err(|_| ())
        .unwrap();

    let context = canvas
        .get_context("2d")
        .unwrap()
        .unwrap()
        .dyn_into::<web_sys::CanvasRenderingContext2d>()
        .unwrap();

    context.begin_path();

    // Draw the outer circle.
    context
        .arc(75.0, 75.0, 50.0, 0.0, f64::consts::PI * 2.0)
        .unwrap();

    // Draw the mouth.
    context.move_to(110.0, 75.0);
    context.arc(75.0, 75.0, 35.0, 0.0, f64::consts::PI).unwrap();

    // Draw the left eye.
    context.move_to(65.0, 65.0);
    context
        .arc(60.0, 65.0, 5.0, 0.0, f64::consts::PI * 2.0)
        .unwrap();

    // Draw the right eye.
    context.move_to(95.0, 65.0);
    context
        .arc(90.0, 65.0, 5.0, 0.0, f64::consts::PI * 2.0)
        .unwrap();

    context.stroke();
}
